-- ----------------------------
-- Table structure for `product`
-- ----------------------------
DROP TABLE IF EXISTS `product`;
CREATE TABLE `product` (
  `pid` int(10) NOT NULL AUTO_INCREMENT,
  `pname` varchar(100) DEFAULT NULL,
  `price` int(10) DEFAULT NULL,
  `scprice` int(10) DEFAULT NULL,
  `ppicture` varchar(200) DEFAULT NULL,
  `pcontent` text,
  `tid` int(10) DEFAULT NULL,
  `hot` bit(1) DEFAULT b'0',
  PRIMARY KEY (`pid`),
  KEY `tid` (`tid`),
  CONSTRAINT `product_ibfk_1` FOREIGN KEY (`tid`) REFERENCES `ttype` (`tid`)
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of product
-- ----------------------------
INSERT INTO `product` VALUES ('1', '美国进口嘉宝奶酪手指玉米芝士泡芙条puffs婴幼儿童宝宝零食饼干', '2180', '1744', '618821895.jpg;618821894.jpg;618821893.png;618821892.jpg;618821891.jpg', '这是一款嘉宝泡芙条，不管是宝妈还是婴儿都可以吃哦~打开包装后一闻到味道就有一种找到真爱的感觉，味道这么出纯正的芝士味的零食实在太少了好吗？天然奶酪制成的泡芙条，含有丰富钙铁锌钾维生素，营养丰富入口即化，宝宝更是喜欢的不得了。\r\n\r\n营养多多更健康\r\n\r\n\r\n奶酪含有丰富蛋白质、钙、脂肪、维生素等\r\n\r\n\r\n玉米含有丰富纤维素和大量的镁\r\n\r\n口感轻盈蓬松，奶酪放到宝宝的嘴里就化了\r\n\r\n不用担心卡到宝宝的喉咙发生危险\r\n', '2', '');
INSERT INTO `product` VALUES ('2', '【澳洲空运】HA 1段2段3段4段雀巢奶粉抗过敏水解蛋白奶粉3罐包邮', '17000', '13600', '81340172.jpg;81340172-2.jpg;81340172-3.jpg;81340172-4.jpg;81340172-5.jpg', '\r\n雀巢HA 价格：1段2段 170/罐  ；3段4段 \r\n  \r\n  \r\n 147/罐\r\n  \r\n  \r\n\r\n  \r\n  \r\n ，亲请联系客服改价\r\n  \r\n  \r\n\r\n  \r\n  \r\n \r\n \r\n \r\n\r\n \r\n\r\n    注意事项 \r\n       \r\n \r\n\r\n\r\n\r\n\r\n\r\n \r\n \r\n本店只提供海外服务，所进口商品符合欧盟食品安全标准，已经国家出入境检验检疫部门抽检合格，报关手续完备，下单即视为同意以上要约。 ', '1', '');
INSERT INTO `product` VALUES ('3', '英国爱他美3段Aptamil婴幼儿配方奶粉三段本土原装进口罐装900g', '12700', '10160', '2731579797.jpg;2731579797-2.jpg', '该产品保质期到2019年9月，效期可能会有上下1个月左右浮动，\r\n\r\n具体以实际收到货为准，不接受的客户请谨慎下单！\r\n\r\n拍下即默认此条例，不得以此理由要求退换货\r\n', '1', '');
INSERT INTO `product` VALUES ('4', '【官方授权4罐包邮包税】澳洲Fresco芙思柯婴幼儿羊奶粉1段900g ', '23200', '18560', '81340173.jpg;81340173-2.jpg;81340173-3.jpg;81340173-4.jpg', '生产日期: 2018年01月10日 至 2018年01月24日', '1', '');
